import JSDC from "../"
import { Control, DomUtil, Icon, latLng, Map, Marker } from "leaflet"

export type Options = {
  position: string,
  Jsdc?: JSDC
}

export type OnAdd = (map: Map) => HTMLDivElement

const iconBase64 = 'data:image/svg+xml;;base64,'
const markerBase64 = 'data:image/png;base64,'
export default Control.extend<{
  options: Options,
  onAdd: OnAdd
}>({
  options: {
    position: 'topright',
  },
  onAdd: function(map: Map) {
    const container = DomUtil.create('div', 'leaflet-bar leaflet-control leaflet-control-custom')
    container.innerHTML = `<img style="width: -webkit-fill-available;padding: 5px;" src="${iconBase64}"/>`

    container.style.backgroundColor = 'white'
    container.style.width = '30px'
    container.style.height = '30px'
    container.style.fontWeight = '800'
    container.style.fontSize = '25px'
    container.style.textAlign = 'center'
    container.style.lineHeight = '25px'
    container.style.cursor = 'pointer'

    let marker: Marker | undefined

    const handlePositionChange: PositionCallback = ({ coords }) => {
      const { latitude, longitude } = coords
      const position = latLng(latitude, longitude)
      this.options.Jsdc?.userGeolocationUpdateEvent.raise(position)
      if (marker) {
        marker.setLatLng(position)
        return
      }
      marker = new Marker(position, { icon: new Icon({ iconUrl: markerBase64, iconSize: [35, 35] }) })
      marker.addTo(map)
    }
    const handlePositionError: PositionErrorCallback = () => console.warn('failed to get geolocation')

    navigator.geolocation.watchPosition(
      handlePositionChange,
      handlePositionError,
      {
        enableHighAccuracy: true,
        timeout: 5000,
        maximumAge: 3000
      }
    )

    container.onclick = () => {
      marker && map.flyTo(marker?.getLatLng()!, 17, { duration: 2 })
    }
    return container
  }
})
